        if (!empty($mensaje)) {
            // Abrir el archivo para escritura (puedes ajustar la ruta segÃºn tu configuraciÃ³n)
            $archivo = fopen("mensajes.txt", "a");

            // Escribir el mensaje en el archivo y agregar un salto de lÃ­nea
