    <?php
    // Verificar si se ha enviado el formulario
    if ($_SERVER["REQUEST_METHOD"] == "POST") {
        // Obtener el mensaje del formulario y limpiar espacios en blanco al inicio y al final
        $mensaje = trim($_POST['txtMensaje']);

        // Verificar que el mensaje no esté vacío antes de guardarlo
        if (!empty($mensaje)) {
            // Abrir el archivo para escritura (puedes ajustar la ruta según tu configuración)
            $archivo = fopen("mensajes.txt", "a");

            // Escribir el mensaje en el archivo y agregar un salto de línea
            fwrite($archivo, $mensaje . PHP_EOL);

            // Cerrar el archivo
            fclose($archivo);
        }
    }

    // Leer y almacenar todos los mensajes existentes
    $mensajes = file("mensajes.txt", FILE_IGNORE_NEW_LINES | FILE_SKIP_EMPTY_LINES);
    ?>

    <form id="mensajeForm" method="post">
        <input type="text" name="txtMensaje" id="txtMensaje" placeholder="Escribe un mensaje...">
        <br/>
        <!-- Agrega este botón de emojis -->
        <button type="button" id="emojiButton">😃</button>
        <input type="submit" value="Enviar">
    </form>

    <div id="mensajesContainer">
        <h2>Mensajes Anonymous</h2>
        <?php
        // Mostrar todos los mensajes
        foreach ($mensajes as $mensaje) {
            echo "<p>$mensaje</p>";
        }
        ?>
    </div>

